Block.body:
{% if feedback.Feedback.alias == 'order_call' %}
    {{ croogo_region alias = "twig_order_call" }}
{% endif %}

{% set flash = _view.loadHelper('Flash') %}
{% set flashOut = flash.render() %}
{% if flashOut %}
    {% set result = {} %}
    {% if flashOut matches '/message success/' %}
        {% set result = {'success': true} %}
    {% else %}
        {% set text = flashOut|replace({'<div id="flashMessage" class="message error">': '', '</div>': ''})|trim %}
        {% set result = {'success': false, text: text} %}
    {% endif %}
    {{ result|json_encode }}
{% else %}
{{ form.create('Review', {
    'url': {'plugin': 'reviews', 'controller': 'reviews', 'action': 'add'},
    'class': 'send-form',
    'autocomplete': 'off',
    'inputDefaults': {'div': 'ani-input-wrap'}
}) }}
    {% set requiredLabel = '<span class="accent-star">*</span>' %}
    {{ form.input('sender', {
        'label': {'class': 'ani-label', 'text': 'Автор ' ~ requiredLabel},
        'class': 'client-name ani-input',
        'type': 'text',
        'required': true,
    }) }}
    {{ form.input('phone', {
        'label': {'class': 'ani-label', 'text': 'Ваш номер телефона ' ~ requiredLabel},
        'class': 'client-phone ani-input',
        'required': true,
        'type': 'tel'
    }) }}
    {{ form.input('email', {
        'label': {'class': 'ani-label', 'text': 'Ваша почта ' ~ requiredLabel},
        'class': 'client-mail ani-input',
        'required': true,
        'type': 'tel'
    }) }}
    {{ form.input('text', {
        'label': {'class': 'ani-label', 'text': 'Ваш отзыв ' ~ requiredLabel},
        'class': 'client-message ani-input',
        'required': true,
        'rows': 3,
        'type': 'textarea'
    }) }}
    {{ form.input('new_picture', {'class': 'client_file','type': 'file', 'label': false, 'div': false}) }}
    <div class="btn-holder">
        <button type="submit" class="btn">Оставить отзыв</button>
        {% if recaptcha.hasKey() %}
            {{ recaptcha.display_form({'id': 'order_call', 'div': 'recaptcha-block'}) }}
        {% endif %}
    </div>
    <div class="form-agree align-left">
        <label class="checkbox-label form-agree-check checked">
            <input class="accent-check" type="checkbox" checked>Настоящим подтверждаю, что ознакомлен и согласен с условиями <a href="#" class="open-text-modal agree-link" data-id="1">политики конфиденциальности.</a>
        </label>
    </div>
{{ form.end() }}
{% endif %}

{% set headerClass = 'layout-header' %}
{% set favoriteButton = false %}
{% set favoriteButtonWrapperClass = '' %}
{% set phoneClass = 'light' %}
{% set logoPath = 'logo.png' %}
{% if (_view.request.controller == 'realty' and (_view.request.action == 'view' or _view.request.action == 'view_slug')) %}
    {% set headerClass = 'layout-header dark' %}
    {% set favoriteButton = true %}
    {% set favoriteButtonWrapperClass = 'selected-count light' %}
{% endif %}
{% if (_view.request.controller == 'favorites' and _view.request.action == 'index') %}
    {% set headerClass = 'layout-header light' %}
    {% set favoriteButton = true %}
    {% set favoriteButtonWrapperClass = 'selected-count dark' %}
    {% set phoneClass = 'dark' %}
    {% set logoPath = 'logo-dark.png' %}
{% endif %}
<header class="{{ headerClass }}">
    <div class="container">
        <div class="row">
            <div class="col-5-12 align-right hidden-xs">
                <a href="tel:{{ config.Site.phone }}" class="phone-link {{ phoneClass }}">
                    <span class="phone-icon-wrap">
                        <svg class="svg-icon phone-icon" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 459 459" xml:space="preserve">
                            <path class="st0" d="M433.5,318.8c-30.6,0-61.2-5.1-91.8-15.3c-7.6-2.5-17.9,0-25.5,5.1l-56.1,56.1 c-71.4-35.7-130.1-96.9-168.3-168.3l56.1-56.1c7.6-7.6,10.2-17.8,5.1-25.5c-7.6-28.1-12.8-58.7-12.8-89.3 c0-15.3-10.2-25.5-25.5-25.5H25.5C10.2,0,0,10.2,0,25.5C0,265.2,193.8,459,433.5,459c15.3,0,25.5-10.2,25.5-25.5v-89.3 C459,329,448.8,318.8,433.5,318.8z"/>
                        </svg>
                    </span> {{ config.Site.phone }}
                </a>
            </div>
            <div class="col-1-6 col-xs-1-2 align-center align-right-mobile">
                <a href="/" class="logo">
                    <picture>
                        <source media="(max-width: 640px)" srcset="/uploads/assets/images/logo-mob.png">
                        <img src="/uploads/assets/images/{{ logoPath }}" alt=" ">
                    </picture>
                </a>
            </div>
            <div class="col-5-12 col-xs-1-2 align-right">
                <div class="si-phone">
                    <a href="/agency/realty/add" class="btn-consult btn-consult-red" data-extra="1">Подать объявление</a>
                    <a href="#" class="open-phone-modal btn-consult" data-extra="2">Заказать звонок</a>

                    {% if not favoriteButton %}
                    <div class="search-form-wrap">
                        {{ form.create('Advertisement', {
                            'url': {'plugin':'agency','controller':'realty','action':'index'},
                            'class': "search-form hide"
                        }) }}
                            {{ form.input('Advertisement.search', {'type': 'text', 'class': 'input-header-search placeholder-light', 'div': false, 'placeholder': 'Поиск по слову', 'label': false}) }}
                            <button class="search-btn">
                                <svg width="20" height="20" class="svg-icon search-icon" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 250.313 250.313" xml:space="preserve">
                                    <path style="fill-rule:evenodd;clip-rule:evenodd;" d="M244.186,214.604l-54.379-54.378c-0.289-0.289-0.628-0.491-0.93-0.76 c10.7-16.231,16.945-35.66,16.945-56.554C205.822,46.075,159.747,0,102.911,0S0,46.075,0,102.911 c0,56.835,46.074,102.911,102.91,102.911c20.895,0,40.323-6.245,56.554-16.945c0.269,0.301,0.47,0.64,0.759,0.929l54.38,54.38 c8.169,8.168,21.413,8.168,29.583,0C252.354,236.017,252.354,222.773,244.186,214.604z M102.911,170.146 c-37.134,0-67.236-30.102-67.236-67.235c0-37.134,30.103-67.236,67.236-67.236c37.132,0,67.235,30.103,67.235,67.236 C170.146,140.044,140.043,170.146,102.911,170.146z" fill="#FFFFFF"/>
                                </svg>
                            </button>
                        {{ form.end() }}
                        <div class="search-icon-wrap">
                            <svg width="20" height="20" class="svg-icon search-icon" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 250.313 250.313" xml:space="preserve">
                                <path style="fill-rule:evenodd;clip-rule:evenodd;" d="M244.186,214.604l-54.379-54.378c-0.289-0.289-0.628-0.491-0.93-0.76 c10.7-16.231,16.945-35.66,16.945-56.554C205.822,46.075,159.747,0,102.911,0S0,46.075,0,102.911 c0,56.835,46.074,102.911,102.91,102.911c20.895,0,40.323-6.245,56.554-16.945c0.269,0.301,0.47,0.64,0.759,0.929l54.38,54.38 c8.169,8.168,21.413,8.168,29.583,0C252.354,236.017,252.354,222.773,244.186,214.604z M102.911,170.146 c-37.134,0-67.236-30.102-67.236-67.235c0-37.134,30.103-67.236,67.236-67.236c37.132,0,67.235,30.103,67.235,67.236 C170.146,140.044,140.043,170.146,102.911,170.146z" fill="#FFFFFF"/>
                            </svg>
                        </div>
                    </div>
                    <a href="mailto:{{ config.Site.email }}" class="mail-link">
                        <span class="mail-link-text">{{ config.Site.email }}</span>
                        <svg class="svg-icon mail-icon" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 477.9 477.9" xml:space="preserve">
                            <path class="st0" d="M460.8,68.3H17.1C7.6,68.3,0,75.9,0,85.3v307.2c0,9.4,7.6,17.1,17.1,17.1h443.7c9.4,0,17.1-7.6,17.1-17.1 V85.3C477.9,75.9,470.2,68.3,460.8,68.3z M432.8,102.4L238.9,251.5L45.1,102.4H432.8z M443.7,375.5H34.1V137.1l194.4,149.5 c6.1,4.7,14.7,4.7,20.8,0l194.4-149.5V375.5z"/>
                        </svg>
                    </a>
                    {% else %}
                        <a href="/favorites" class="{{ favoriteButtonWrapperClass }}">
                            <svg class="svg-icon star-icon" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 512 512">
                                <path class="st0" d="M118.9,327.7l-27,165.7c-2.2,13.2,11.9,23,23.5,16.6L256,432.3L396.6,510c11.5,6.4,25.7-3.3,23.5-16.6 l-27-165.7l114.3-117.2c9.1-9.3,3.9-25-9-27l-157.4-24L270.5,9.2c-5.3-11.2-23.7-11.2-29,0L171,159.5l-157.4,24 c-12.9,2-18.1,17.7-9,27L118.9,327.7z" fill="#F8E24E"/>
                            </svg> В избранном (<span class="favorite-counter">0</span>)
                        </a>
                    {% endif %}
                </div>
            </div>
        </div>
    </div>
</header>

{% set flash = _view.loadHelper('Flash') %}
{% set flashOut = flash.render() %}
{% if flashOut %}
    {% set result = {} %}
    {% if flashOut matches '/message success/' %}
        {% set result = {'success': true, 'original': flashOut} %}
    {% else %}
        {% set text = flashOut|replace({'<div id="flashMessage" class="message error">': '', '</div>': ''})|trim %}
        {% set result = {'success': false, 'original': flashOut, text: text} %}
    {% endif %}
    {{ result|json_encode }}
{% else %}
{{ form.create('FeedbackMessage', {
    'url': {'plugin': 'feedback', 'controller': 'feedback', 'action': 'send', '0': 'order_call'},
    'id': 'FeedbackMessageSendFormOrderCall',
    'class': 'send-form',
    'autocomplete': 'off',
    'inputDefaults': {'div': 'ani-input-wrap'}
}) }}
    {{ form.input('name', {
        'id': 'FeedbackMessageNameOrderCall',
        'label': {'class': 'ani-label', 'text': 'Имя *'},
        'class': 'client-name ani-input',
        'type': 'text',
        'required': true,
    }) }}
    {{ form.input('phone', {
        'label': {'class': 'ani-label', 'text': 'Телефон *'},
        'id': 'FeedbackMessagePhoneOrderCall',
        'class': 'client-phone ani-input',
        'required': true,
        'type': 'tel'
    }) }}
    {{ form.input('message', {
        'label': {'class': 'ani-label', 'text': 'Ваше сообщение *'},
        'id': 'FeedbackMessageMessageOrderCall',
        'class': 'client-message',
        'required': true,
        'rows': 3,
        'type': 'textarea'
    }) }}
<div class="btn-holder">
    <button type="submit" class="btn">Оставить заявку</button>
    {% if recaptcha.hasKey() %}
        {{ recaptcha.display_form({'id': 'order_call', 'div': 'recaptcha-block'}) }}
    {% endif %}
</div>
<div class="form-agree align-left">
    <label class="checkbox-label form-agree-check checked">
        {{ form.checkbox('confirmation', {
            'checked': true,
            'id': 'FeedbackMessageConfirmationOrderCall',
            'hiddenField': false,
            'class': 'accent-check',
            'value': 1,
        }) }} Настоящим подтверждаю, что ознакомлен и согласен с условиями <a href="#" class="open-text-modal agree-link" data-id="1">политики конфиденциальности.</a>
    </label>
</div>
{{ form.end() }}
{% endif %}

{{ _view.assign('title', adSeo.titleList()) }}
<section class="section-choice-case" id="choice-case">
    <div class="container">
        <div class="add-case-block">
            <div class="title-normal title-min-size"><h2>Выберите вариант</h2></div>
            {{ form.create('Advertisement', {'url': {'plugin' : 'agency','controller' : 'realty', 'action' : 'index'}}) }}
                <div class="choice-option-case">
                    <div class="choice-label-wrap">
                        {{ form.radio('rent', {'sale': 'Продажа','rent': 'Аренда'}, {
                            'legend': false,
                            'label': {'class': 'choice-label choice-label-big'},
                            'hiddenField': false,
                            'separator': '</div><div class="or">или</div><div class="choice-label-wrap">',
                        }) }}
                    </div>
                </div>
                <div class="show-map-block show-map-choice-case"><div class="show-map-title">Карта</div><div class="show-map-btn"></div></div>
                <div class="choice-case-title">Вид недвижимости</div>
                <ul class="choice-type-case-list not-marked nav">
                    <li>
                        {{ form.radio('category_id', categories, {
                            'legend': false,
                            'label': {'class': 'choice-label choice-label-min'},
                            'hiddenField': false,
                            'separator': '</li><li>',
                        }) }}
                    </li>
                </ul>
                <div class="hidden-params" style="display: none">
                    <div class="row select-block">
                        {{ searchView.renderField('Advertisement.district_id', attribute(searchFields, 'Advertisement.district_id')|merge({'div': 'col-1-4 col-xs-1-2', 'multiple': false, 'empty': true, 'data-allow-clear': "true"}) ) }}
                        {{ searchView.renderField('Advertisement.locality_id', attribute(searchFields, 'Advertisement.locality_id')|merge({'div': 'col-1-4 col-xs-1-2', 'multiple': false, 'empty': true, 'data-allow-clear': "true"}) ) }}
                        {{ searchView.renderField('Advertisement.sub_locality_id', attribute(searchFields, 'Advertisement.sub_locality_id')|merge({'div': 'col-1-4 col-xs-1-2', 'multiple': false, 'empty': true, 'data-allow-clear': "true"}) ) }}
                        {{ searchView.renderField('Advertisement.street_id', attribute(searchFields, 'Advertisement.street_id')|merge({'div': 'col-1-4 col-xs-1-2', 'multiple': false, 'empty': true, 'data-allow-clear': "true"}) ) }}
                    </div>
                    {% set complexCategorySearch = _view.loadHelper('ComplexCategories.ComplexCategorySearch') %}

                    {% set slidersFields = {} %}
                    {% set selectFields = {} %}
                    {% set checkboxFields = {} %}
                    {% set textFields = {} %}

                    {% for searchFieldAlias, searchFieldItem in searchFields %}
                        {% if searchFieldAlias not in ['Advertisement.category_id', 'Advertisement.country_id', 'Advertisement.location_region_id', 'Advertisement.district_id', 'Advertisement.locality_id', 'Advertisement.sub_locality_id', 'Advertisement.micro_district_id', 'Advertisement.street_id', 'Advertisement.metro_id', 'Advertisement.house', 'Advertisement.rent','Advertisement.term_rent_id','Advertisement.id','Advertisement.show_in_hot','Advertisement.exclusive','Advertisement.polygon','Advertisement.currency_id','Advertisement.search','Advertisement.reservation'] %}
                            {% if attribute(searchFieldItem, 'param-type') == -1 %}
                                {% set checkboxFields = checkboxFields|merge({(searchFieldAlias): searchFieldItem}) %}
                            {% endif %}
                            {% if attribute(searchFieldItem, 'param-type') == 0 %}
                                {% set slidersFields = slidersFields|merge({(searchFieldAlias): searchFieldItem}) %}
                            {% endif %}
                            {% if attribute(searchFieldItem, 'param-type') == 1 %}
                                {% set textFields = textFields|merge({(searchFieldAlias): searchFieldItem}) %}
                            {% endif %}
                            {% if attribute(searchFieldItem, 'param-type') == 2 %}
                                {% set selectFields = selectFields|merge({(searchFieldAlias): searchFieldItem})  %}
                            {% endif %}
                        {% endif %}
                    {% endfor %}
                    {% for slidersBatch in slidersFields|batch(2) %}
                        <div class="row range-price-space-block">
                            {% for alias,field in slidersBatch %}
                                {% set alias = alias|replace({'Advertisement.': ''}) %}
                                <div class="col-1-2 col-xs-1 {{ complexCategorySearch.getClassesFilter(null, field)|join(' ')|replace({'input ': ''}) }}">
                                    <div class="choice-case-title">{{ field.label }}</div>
                                    <div class="case-range case-range-{{ alias }}">
                                        {{ form.hidden('Advertisement.' ~ alias ~ '_from', {'class': 'filter-from'} )}}
                                        {{ form.hidden('Advertisement.' ~ alias ~ '_to', {'class': 'filter-to'} )}}
                                        <div class="case-range-{{ alias }}-min"></div>
                                        <div class="case-range-{{ alias }}-max"></div>
                                    </div>
                                    <div class="params-slider slider-range" data-field="{{ alias }}" data-min="{{ attribute(searchView.getRange({'alias': alias}), 'min') }}" data-max="{{ attribute(searchView.getRange({'alias': alias}), 'max') }}" data-step="{{ attribute(searchView.getRange({'alias': alias}), 'step') }}"></div>
                                </div>
                            {% endfor %}
                        </div>
                    {% endfor %}
                    {% for selectBatch in selectFields|batch(4) %}
                        <div class="row select-block">
                            {% for alias,field in selectBatch %}
                                <div class="col-1-4 col-xs-1-2 {{ complexCategorySearch.getClassesFilter(null, field)|join(' ')|replace({'input ': ''}) }}">
                                    {{ form.input(alias, {
                                        'div': false,
                                        'type': 'select',
                                        'label': false,
                                        'empty': true,
                                        'data-placeholder': field.label,
                                        'data-allow-clear': "true",
                                        'options': _view.get(attribute(field, 'data-options')),
                                    }) }}
                                </div>
                            {% endfor %}
                        </div>
                    {% endfor %}
                    {% for textBatch in textFields|batch(4) %}
                        <div class="row">
                            {% for alias,field in textBatch %}
                                <div class="col-1-4 col-xs-1-2 {{ complexCategorySearch.getClassesFilter(null, field)|join(' ')|replace({'input ': ''}) }}">
                                    {{ form.input(alias, {
                                        'div': false,
                                        'label': false,
                                        'type': 'text',
                                        'placeholder': field.label,
                                    }) }}
                                </div>
                            {% endfor %}
                        </div>
                    {% endfor %}
                    <div class="row">
                        <div class="col-1-4 col-xs-1-2">
                            <ul class="check-list-case not-marked">
                                {% for alias,checkbox in checkboxFields %}
                                    <li class="{{ complexCategorySearch.getClassesFilter(null, checkbox)|join(' ')|replace({'input ': ''}) }}">
                                        <label>
                                            {{ form.checkbox('Advertisement.' ~ attribute(checkbox, 'param-id'), {
                                                'hidden': true,
                                                'div': false,
                                                'label': false,
                                                'hiddenField': false,
                                            }) }} {{ checkbox.label }}
                                        </label>
                                    </li>
                                {% endfor %}
                            </ul>
                        </div>
                    </div>
                </div>
                <div class="row filter-control-block">
                    <div class="col-1-6 col-xs-1">
                        <button type="reset" class="filter-default">
                            Сбросить фильтр <svg width="34" height="34" class="svg-icon cross-icon" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 64 64">
                                <path class="st0" d="M28.9,31.8L0.6,60.1c-0.8,0.8-0.8,2.1,0,2.8C1,63.4,1.5,63.6,2,63.6s1-0.2,1.4-0.6L32,34.4L60.5,63 c0.4,0.4,0.9,0.6,1.4,0.6s1-0.2,1.4-0.6c0.8-0.8,0.8-2.1,0-2.8L35.1,31.8L63.4,3.4c0.8-0.8,0.8-2.1,0-2.8c-0.8-0.8-2.1-0.8-2.8,0 L32,29.1L3.4,0.6c-0.8-0.8-2.1-0.8-2.8,0c-0.8,0.8-0.8,2.1,0,2.8L28.9,31.8z" fill="#fff"/>
                            </svg>
                        </button>
                    </div>
                    <div class="col-1-3 col-xs-1 align-center align-left-mobile">
                        <button type="button" class="filter-option-hide hide"><span>показать часть параметров</span><div class="icon"></div></button>
                    </div>
                    <div class="col-1-4 col-xs-1">
                        <div class="filter-num-result">Найдено <b>{{ paginator.params.current }}</b> вариантов</div>
                    </div>
                    <div class="col-1-4 col-xs-1">
                        <button type="submit" class="filter-default">Найти</button>
                    </div>
                </div>
            {{ form.end() }}
            <div class="choice-case-map hide">
                {{ html.script(['https://api-maps.yandex.ru/2.1/?lang=ru_RU', 'Agency.filter-map-site', 'YandexMaps.simplify'], {'inline': false}) }}
                <div class="map-choice-wrap" id="map-list"></div>
                {{ html.scriptStart({'inline': false, 'safe': false}) }}
                let KlasterIs2bMapSite = {
                    url: null,
                    init: function (YandexMapCenter, url) {
                        this.url = url;
                        Is2bMap.init('map-list', YandexMapCenter, [], {'isAdmin': false});
                        let d = '';
                        this.loadObjects(d, true);

                        Is2bMap.map.events.add('boundschange', function (e) {
                            if (Is2bMap.block.hasClass('is2b-loaded-points')) {
                                return !1;
                            }
                            d = Is2bMap.dataMapKlaster();
                            KlasterIs2bMapSite.loadObjects(d, false);
                        });
                    },
                    setBounds: function () {
                        if (Is2bMap.geoObjects != null) {
                            if (Is2bMap.geoObjects.length > 0) {
                                console.log('set Bounsds');
                                console.log(Is2bMap.clusterer.getBounds());
                                Is2bMap.map.setBounds(Is2bMap.clusterer.getBounds(), {
                                    checkZoomRange: true
                                });
                            }
                        }
                    },
                    loadObjects: function (d, setBounds) {
                        Is2bMap.removeAllPoints();
                        $.ajax({
                            url: this.url + (d === '' ? '' : '&' + d),
                            dataType: 'json'
                        }).done(function (data) {
                            console.log(data.length);
                            Is2bMap.setPoints(data);
                            if (setBounds) {
                                KlasterIs2bMapSite.setBounds();
                            }
                        });
                    }
                };
                ymaps.ready(function () {
                    KlasterIs2bMapSite.init({{ yaMaps.YandexMapCenter() }}, window.location.protocol + '//' + window.location.hostname + '/agency/realty/map' + (window.location.search || '?1=1'));
                })
                {{ html.scriptEnd() }}
            </div>
            <div class="result-items-case">
                <div class="row row-3 case-items-block">
                    {{ adView.setDefaults({default_picture: config.Site.no_photo_image}) }}
                    {% for item in list %}
                        {% set offerItem = adView.getInfo(item) %}
                        <div class="col-1-3 col-xs-1">
                            <a href="{{ html.url(offerItem.url) }}" class="case-item" title="{{ adSeo.pattern_title(item)|e }}">
                                {% set favoriteActiveClass = '' %}
                                {% set favoriteAction = 'add' %}
                                {% if favorite.has('Advertisement', item.Advertisement.id) %}
                                    {% set favoriteActiveClass = ' active' %}
                                    {% set favoriteAction = 'remove' %}
                                {% endif %}
                                {{ html.tag('div', '', {
                                    'class': 'favorites-action star' ~ favoriteActiveClass,
                                    'escape': false,
                                    'data-model': 'Advertisement',
                                    'data-id': item.Advertisement.id,
                                    'data-action': favoriteAction
                                }) }}
                                <div class="case-id">id {{ item.Advertisement.id }}</div>
                                <picture class="case-image-wrap">
                                    {% set gallery = adView.getGallery(item) %}
